INSERT INTO `login_bonus_message_data` VALUES (/*id*/416, /*login_bonus_id*/80005, /*type*/2, /*day_count*/7, /*luck_pattern*/0, /*rate*/0, /*character_id*/106401, /*character_name*/'雪菲', /*message*/'好像很有趣……我也好想一起试试啊。\n妈妈……不、不对！\n可可萝小姐，我可以加入吗？', /*voice_id*/18, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/417, /*login_bonus_id*/80005, /*type*/1, /*day_count*/7, /*luck_pattern*/0, /*rate*/0, /*character_id*/105901, /*character_name*/'可可萝', /*message*/'当然可以，\n大家一起和睦地学习吧。', /*voice_id*/17, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/418, /*login_bonus_id*/80005, /*type*/0, /*day_count*/7, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'教育妈妈\n若无其事地诞生了？', /*voice_id*/25, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/419, /*login_bonus_id*/80005, /*type*/3, /*day_count*/7, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'学习是很重要的事情哦！\n小凯露也一起来\n努力学习吧！', /*voice_id*/23, /*additional_type*/0, /*additional_param*/'');
