INSERT INTO `login_bonus_message_data` VALUES (/*id*/421, /*login_bonus_id*/80005, /*type*/0, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'距离3.5周年还有7天♪\n哼哼♪\n哼哼哼♪', /*voice_id*/27, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/422, /*login_bonus_id*/80005, /*type*/2, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/106401, /*character_name*/'雪菲', /*message*/'那个，\n可可萝小姐，\n凯露这是怎么了？', /*voice_id*/19, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/423, /*login_bonus_id*/80005, /*type*/1, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/105901, /*character_name*/'可可萝', /*message*/'她心情非常好，\n正在哼歌呢。\n耳朵和尾巴都很有节奏地摇摆着……\n看起来非常有兴致。', /*voice_id*/18, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/424, /*login_bonus_id*/80005, /*type*/3, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'这是因为！\n她拿到了慈乐之音\n夏日演出的\n门票哦♪', /*voice_id*/24, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/425, /*login_bonus_id*/80005, /*type*/2, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/106401, /*character_name*/'雪菲', /*message*/'诶诶！？\n我记得那场演出的门票\n很难入手啊……', /*voice_id*/20, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/426, /*login_bonus_id*/80005, /*type*/0, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'哼哼哼哼♪\n闪亮未来♪', /*voice_id*/28, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/427, /*login_bonus_id*/80005, /*type*/1, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/105901, /*character_name*/'可可萝', /*message*/'原来如此……\n那么凯露小姐这么开心，\n也不难理解了呢', /*voice_id*/19, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/428, /*login_bonus_id*/80005, /*type*/2, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/106401, /*character_name*/'雪菲', /*message*/'但是慈乐之音的\n《闪亮未来》\n里面应该没有闪亮未来\n这句歌词啊……', /*voice_id*/21, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/429, /*login_bonus_id*/80005, /*type*/0, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'哼哼……啊！？\n你、你们几个！\n怎么随随便便\n听别人哼的歌啊！？', /*voice_id*/29, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/430, /*login_bonus_id*/80005, /*type*/0, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'这、这不是\n慈乐之音的歌……\n是我的原创曲哦！\n你们再听我就要收费了！？', /*voice_id*/30, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/431, /*login_bonus_id*/80005, /*type*/3, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/105801, /*character_name*/'佩可莉姆', /*message*/'诶嘿嘿，小凯露，\n在慈乐之音的夏日演出上，\n你要玩得尽兴哦☆', /*voice_id*/25, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/432, /*login_bonus_id*/80005, /*type*/0, /*day_count*/8, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'讨厌～\n别用这么温柔的眼神看我！\n宰了你哦！', /*voice_id*/31, /*additional_type*/0, /*additional_param*/'');
INSERT INTO `login_bonus_message_data` VALUES (/*id*/433, /*login_bonus_id*/80005, /*type*/0, /*day_count*/9, /*luck_pattern*/0, /*rate*/0, /*character_id*/106401, /*character_name*/'雪菲', /*message*/'距离3.5周年还有6天。\n虽然其他三人也说过，\n但时间真是一眨眼就过去了呢。', /*voice_id*/22, /*additional_type*/0, /*additional_param*/'');
