INSERT INTO `login_bonus_message_data` VALUES (/*id*/497, /*login_bonus_id*/80005, /*type*/0, /*day_count*/14, /*luck_pattern*/0, /*rate*/0, /*character_id*/106001, /*character_name*/'凯露', /*message*/'哼哼♪\n到时候要给你吃\n让猫都会炸毛的各种食物，\n建议你做好觉悟哦？', /*voice_id*/41, /*additional_type*/0, /*additional_param*/'');
