INSERT INTO `navi_comment` VALUES (/*comment_id*/24001, /*where_type*/24, /*character_id*/109001, /*face_type*/1, /*character_name*/'惠理子（情人节）', /*description*/'您帮忙制作的\n巧克力蛋糕，\n怎么可能不好吃呢。', /*voice_id*/1, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/4.08, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24002, /*where_type*/24, /*character_id*/109001, /*face_type*/1, /*character_name*/'惠理子（情人节）', /*description*/'您不可能会失败的。\n请试着再挑战一次吧。', /*voice_id*/2, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/5.0, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24003, /*where_type*/24, /*character_id*/109001, /*face_type*/1, /*character_name*/'惠理子（情人节）', /*description*/'这个蛋糕\n是想要送给您的。\n没想到\n您竟然会来帮忙……嘻嘻。', /*voice_id*/3, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/10.11, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24004, /*where_type*/24, /*character_id*/109001, /*face_type*/1, /*character_name*/'惠理子（情人节）', /*description*/'您不用太气馁。\n接下来，\n我会好好花时间教您的。', /*voice_id*/4, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/4.0, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24005, /*where_type*/24, /*character_id*/109001, /*face_type*/1, /*character_name*/'惠理子（情人节）', /*description*/'您这次……是状态不太好吗？\n只要是您帮忙做的东西，\n不管做成什么样，我都不在意……嘻嘻。', /*voice_id*/5, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/6.0, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24006, /*where_type*/24, /*character_id*/109002, /*face_type*/1, /*character_name*/'惠理子（情人节）', /*description*/'您亲手做的巧克力蛋糕，\n对我来说就是宝物……\n我不会让侮辱它的人活下来的。', /*voice_id*/6, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24007, /*where_type*/24, /*character_id*/109002, /*face_type*/1, /*character_name*/'惠理子（情人节）', /*description*/'您做得真好，太了不起了！\n您做的巧克力蛋糕\n和您非常相称。', /*voice_id*/7, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24008, /*where_type*/24, /*character_id*/109002, /*face_type*/1, /*character_name*/'惠理子（情人节）', /*description*/'愿意帮助我的您\n已经成为了我的俘虏……\n我会邀请您前往甜美的世界。', /*voice_id*/8, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
