INSERT INTO `navi_comment` VALUES (/*comment_id*/24010, /*where_type*/24, /*character_id*/109002, /*face_type*/1, /*character_name*/'惠理子（情人节）', /*description*/'这是世界上\n最棒的巧克力蛋糕。\n因为，\n这是您精心制作出来的。', /*voice_id*/10, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24011, /*where_type*/24, /*character_id*/109101, /*face_type*/1, /*character_name*/'静流（情人节）', /*description*/'没能做好，你觉得很可惜？\n不过，弟弟的心意让我很开心哦。\n不管什么时候，\n姐姐都会在你身边的♪', /*voice_id*/1, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/2.12, /*change_face_type*/2, /*event_id*/10011);
