INSERT INTO `navi_comment` VALUES (/*comment_id*/24013, /*where_type*/24, /*character_id*/109101, /*face_type*/2, /*character_name*/'静流（情人节）', /*description*/'你帮了姐姐的忙，\n做得很好哦！\n啊～\n现在还不能偷吃哦！', /*voice_id*/3, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/2.3, /*change_face_type*/4, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24014, /*where_type*/24, /*character_id*/109102, /*face_type*/1, /*character_name*/'静流（情人节）', /*description*/'快看快看！\n蛋糕胚烤得很好看哦。\n真不愧是弟弟！', /*voice_id*/4, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24015, /*where_type*/24, /*character_id*/109101, /*face_type*/5, /*character_name*/'静流（情人节）', /*description*/'好啦好啦，别那么难过。\n虽然看起来……挺有个性的，\n但是味道肯定不会差！', /*voice_id*/5, /*start_time*/'2019/01/31 12:00:00', /*end_time*/'2030/12/17 14:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/5.23, /*change_face_type*/2, /*event_id*/10011);
