INSERT INTO `navi_comment` VALUES (/*comment_id*/24010, /*where_type*/24, /*character_id*/109002, /*face_type*/1, /*character_name*/'惠理子（情人节）', /*description*/'这是世界上最棒的\n巧克力蛋糕\n因为，这是您精心\n制作的', /*voice_id*/10, /*start_time*/'2020/01/01 0:00:00', /*end_time*/'2099/12/31 23:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24011, /*where_type*/24, /*character_id*/109101, /*face_type*/1, /*character_name*/'静流（情人节）', /*description*/'没能顺利完成，觉得很可惜？\n但是，弟弟的心意让我觉得很开心\n不管什么时候，姐姐\n都会在你身边的哦♪', /*voice_id*/1, /*start_time*/'2020/01/01 0:00:00', /*end_time*/'2099/12/31 23:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/2.12, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24012, /*where_type*/24, /*character_id*/109101, /*face_type*/5, /*character_name*/'静流（情人节）', /*description*/'嗯，难得见到弟弟你\n这么紧张啊？\n要不要再试试？', /*voice_id*/2, /*start_time*/'2020/01/01 0:00:00', /*end_time*/'2099/12/31 23:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/4.19, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24013, /*where_type*/24, /*character_id*/109101, /*face_type*/2, /*character_name*/'静流（情人节）', /*description*/'你帮了姐姐\n的大忙！\n啊——\n不能偷吃哦！', /*voice_id*/3, /*start_time*/'2020/01/01 0:00:00', /*end_time*/'2099/12/31 23:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/2.3, /*change_face_type*/4, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24014, /*where_type*/24, /*character_id*/109102, /*face_type*/1, /*character_name*/'静流（情人节）', /*description*/'快看快看！\n海绵蛋糕烤得很好看哦\n真不愧是弟弟！', /*voice_id*/4, /*start_time*/'2020/01/01 0:00:00', /*end_time*/'2099/12/31 23:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24015, /*where_type*/24, /*character_id*/109101, /*face_type*/5, /*character_name*/'静流（情人节）', /*description*/'好啦好啦，不要那么难过\n虽然看起来……挺有个性的，\n但是味道肯定不会差！', /*voice_id*/5, /*start_time*/'2020/01/01 0:00:00', /*end_time*/'2099/12/31 23:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/5.23, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24016, /*where_type*/24, /*character_id*/109102, /*face_type*/1, /*character_name*/'静流（情人节）', /*description*/'像弟弟一样可爱的\n蛋糕做好了！\n快点来尝尝看吧♪', /*voice_id*/6, /*start_time*/'2020/01/01 0:00:00', /*end_time*/'2099/12/31 23:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24017, /*where_type*/24, /*character_id*/109101, /*face_type*/1, /*character_name*/'静流（情人节）', /*description*/'咦？　弟弟，你的指尖\n沾到巧克力了哦？\n姐姐来帮你舔掉吧，\n唔嗯！', /*voice_id*/7, /*start_time*/'2020/01/01 0:00:00', /*end_time*/'2099/12/31 23:59:59', /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/5.01, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24018, /*where_type*/24, /*character_id*/109102, /*face_type*/1, /*character_name*/'静流（情人节）', /*description*/'越来越像模像样了呀？\n不过弟弟还能做得更好的！\n姐姐相信你哦', /*voice_id*/8, /*start_time*/'2020/01/01 0:00:00', /*end_time*/'2099/12/31 23:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24019, /*where_type*/24, /*character_id*/109102, /*face_type*/1, /*character_name*/'静流（情人节）', /*description*/'哇～\n看起来很好吃！\n弟弟已经很熟练了，\n姐姐能放心地交给你了呢！', /*voice_id*/9, /*start_time*/'2020/01/01 0:00:00', /*end_time*/'2099/12/31 23:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
