INSERT INTO `psy_note` VALUES (/*psy_product_id*/100, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'佩可莉姆的布丁', /*flavor_1*/'是公主般的布丁，\n把店里能吃的东西\n都尽可能地加进去了，\n好期待会是什么味道。', /*flavor_2*/'因为实在是太好吃了，\n吃一口就停不下来，是最棒的\n布丁。已经没有人能阻止\n现在的宫子了。', /*flavor_3*/'超棒的对吧☆', /*disp_order*/1, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/101, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'可可萝的布丁', /*flavor_1*/'总是陪伴在你身边的\n那个人的布丁。就连宫子的\n任性要求都会\n温柔接纳。', /*flavor_2*/'吃起来会有种\n很温柔的感觉……\n是从早到晚都会和你在\n一起的味道……', /*flavor_3*/'是主人！', /*disp_order*/2, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/102, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'凯露的布丁', /*flavor_1*/'是那个傲娇女孩的\n布丁。我试着把砂糖和香\n料混在了一起。到底会\n是什么味道，好期待。', /*flavor_2*/'可以一口气尝到\n很多种口味哦。把傲娇做\n成布丁会很好吃呢。宫子\n也要试试做傲娇。', /*flavor_3*/'小心我宰了你哦！这样！\n娇的部分在哪里？', /*disp_order*/3, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/103, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'雪菲的布丁', /*flavor_1*/'曾经和你一样\n变得像婴儿一样的那个人的\n布丁。这次试着做成了\n冰冰的布丁雪糕。', /*flavor_2*/'在温暖的地方吃布丁雪糕\n真是最棒了♪因为太过安心，\n就算是不记得过去的宫子，\n内心都要变成小孩子了～', /*flavor_3*/'哥哥♪', /*disp_order*/4, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/104, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'伊莉亚的布丁', /*flavor_1*/'看着伊莉亚想到的布丁，\n用了番茄汁\n来提味哦。很有营养呢。', /*flavor_2*/'有了番茄，在补充营养方面\n也很完美。全身渐渐充满\n了力量，复活也不再是梦\n想了。宫子要是复活了…', /*flavor_3*/'哈哈哈哈！\n就要征服世界了！', /*disp_order*/5, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/105, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'茜里的布丁', /*flavor_1*/'这是茜里的布丁。做得\n和依里差不多\n可爱。老实说，\n宫子只要好吃就够了。', /*flavor_2*/'就算外形可爱，宫子也\n不会降低对味道的要求。\n嗯……这、这是……有种\n很奇怪的感觉……', /*flavor_3*/'哥哥是个大色鬼！就像这样！', /*disp_order*/6, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/106, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'依里的布丁', /*flavor_1*/'要是做得不可爱，感觉\n茜里会生气，所以把依里\n的布丁做得很可爱。\n是我的自信之作哦。', /*flavor_2*/'嗯……这个布丁很难\n评价啊……一方面想\n坦白地说……另一方面\n又想藏在心底……', /*flavor_3*/'呃……那个……\n我、我最讨厌你了！', /*disp_order*/7, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/107, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'忍的布丁', /*flavor_1*/'因为忍总是对宫子很温柔，\n所以宫子做得很认真。\n因为不需要骷髅老爸\n所以没有做。', /*flavor_2*/'只要吃下这个布丁，宫子\n就有种被忍附身的感觉呢！\n只要有忍的力量，就能随\n心所欲地看未来的布丁！', /*flavor_3*/'爸爸……\n你也该适可而止了吧！\n嗯？没能看见……\n布丁的未来……', /*disp_order*/8, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/108, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'纯的布丁', /*flavor_1*/'是那个总是穿着黑色铠甲\n的人的布丁。本来是想做\n得硬一点的，结果硬过头\n了。', /*flavor_2*/'因为太硬了，所以咬不动，\n只能整个吞下去。感觉自\n己的身体也变硬了。有种\n能保护大家的感觉！', /*flavor_3*/'炼狱火海之盾！就像这样！', /*disp_order*/9, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/109, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'智的布丁', /*flavor_1*/'是那个总是捉弄你的人的\n布丁。用了\n一种有些奇怪的香料\n来表现那种捉弄。', /*flavor_2*/'会有种香料在嘴里炸开的\n感觉，很新颖的口感对吧！\n爆炸一般的口感让宫子\n有种被玩弄的感觉！', /*flavor_3*/'和你在一起我就会失常……\n难道说真正在玩弄他人的人\n是你？', /*disp_order*/10, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/110, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'茉莉的布丁', /*flavor_1*/'是那个在王宫骑士团的\n小不点。据说虽然个子小，\n但很能干，所以调味\n偏重一点。', /*flavor_2*/'嗯～和外表不相称的浓郁风味\n充斥了整个嘴巴，\n让人印象深刻！', /*flavor_3*/'英雄虎爆弹！\n就像这样！', /*disp_order*/11, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/111, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'克罗依的布丁', /*flavor_1*/'超级冷酷的布丁，\n感觉有些\n缺乏激情。这个\n真的可以吃吗？', /*flavor_2*/'明明看上去很冷酷，\n但味道非常温柔！有种\n令人怀念的感觉……', /*flavor_3*/'嗯，也不错吧？\n虽然我不是很懂。', /*disp_order*/13, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/112, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'千爱瑠的布丁', /*flavor_1*/'装饰得很漂亮哦！\n色彩鲜艳，看上去很好吃，\n看来很值得期待。', /*flavor_2*/'口感有些独特……\n恰噜……抽噜……切噜？\n有种切噜的感觉，\n但切噜到底是什么啊？', /*flavor_3*/'是布丁★吧！', /*disp_order*/14, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/113, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'优妮的布丁', /*flavor_1*/'外形很完美！\n做出这个布丁的人\n一定是天才！\n真想快点吃啊！', /*flavor_2*/'味道怎么有点\n酸酸的……\n没尝过这种味道，\n这个味道难道是……', /*flavor_3*/'直白一点说，\n就是青春的味道？', /*disp_order*/15, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/114, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'咲恋的布丁', /*flavor_1*/'把你说的那些味道令人怀\n念的东西全都加进去试了\n一下，你明明没有记忆，\n怎么还会觉得怀念啊？', /*flavor_2*/'宫子明明没有\n变成幽灵之前的记忆，\n都觉得有点怀念了……', /*flavor_3*/'你是……我的青梅竹马吗？', /*disp_order*/16, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/115, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'铃莓的布丁', /*flavor_1*/'是一个偶然遇见的女仆\n教我做的。女仆\n应该是会做布丁的。\n但我怎么觉得这么不安？', /*flavor_2*/'好奇怪。吃了这个布丁\n之后，宫子摔跤三次了。\n宫子是幽灵，本来是不会\n摔跤的。为什么呢？', /*flavor_3*/'呀啊啊啊啊！', /*disp_order*/17, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/116, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'胡桃的布丁', /*flavor_1*/'是一个胆小的孩子教我做的，\n是一种可爱的小布丁。\n明明没碰到都会晃动，\n真是惊人的柔软度。', /*flavor_2*/'甜中带咸，\n让人上瘾。\n这个布丁只要加把劲，\n一定可以变成各种美味。', /*flavor_3*/'成竟事者志有！就像这样！', /*disp_order*/18, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/117, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'绫音的布丁', /*flavor_1*/'看着熊玩偶\n想到的。虽然小，\n但有种稳定的甜味，\n能让人打起精神来！', /*flavor_2*/'一吃这种布丁，\n就有种变成姐姐\n的感觉。危机布丁\n就由宫子来守护！', /*flavor_3*/'布丁全力挥击！', /*disp_order*/19, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/118, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'真步的布丁', /*flavor_1*/'真步真步王国的公主\n的布丁，\n甚至俘虏了魔物的胃，\n是一种很棒的布丁。', /*flavor_2*/'充满了童话般的力量，\n现在的话，感觉宫子\n也能使用魔法！\n来，要上了哦！', /*flavor_3*/'神奇宫子魔法灵，\n咕噜灵波～', /*disp_order*/20, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/119, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'真琴的布丁', /*flavor_1*/'为了做出帅气的布丁，\n加了很多野性的\n材料。一定能做出\n一个热血的布丁。', /*flavor_2*/'唔噢！宫子\n感觉自己变强了！\n如果遇上麻烦，\n尽管交给宫子来处理吧！', /*flavor_3*/'天狼噬斩！', /*disp_order*/21, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/120, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'香织的布丁', /*flavor_1*/'我把某国很容易采集到的\n一种长得像苦瓜的植物\n加进去了！虽然\n好像有点苦……', /*flavor_2*/'但只要吃一口，\n就能涌现出无尽的活力！\n我心情好，\n给你跳支舞吧！', /*flavor_3*/'一切都会好起来的！\n就像这样！', /*disp_order*/22, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/121, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'香澄的布丁', /*flavor_1*/'我加了很多直觉敏锐的人\n喜欢吃的东西，\n听说在思考难题的时候很\n适合吃，为什么啊？', /*flavor_2*/'唔唔唔……原来如此，\n大量高质量的糖分\n能让大脑活性化！', /*flavor_3*/'呵呵，这是很简单的推理。', /*disp_order*/23, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/122, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'真阳的布丁', /*flavor_1*/'这个布丁里加了很多\n鲜榨牛奶哦～\n是宫子自己亲手挤的奶哦！\n真阳也有帮忙。', /*flavor_2*/'奶味非常浓郁，\n好吃到我舌头都要化了♪\n从这个布丁能感受到牧场……\n感受到自然的气息！', /*flavor_3*/'伊丽莎白～就像这样！', /*disp_order*/24, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/123, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'栞的布丁', /*flavor_1*/'听真阳说，她是个温柔的\n人。为了表现她的温柔，\n我把甜味控制得\n比较温和。', /*flavor_2*/'尝起来有种隐约的甜味，\n但同时也能让人\n感觉到坚定的信念，\n是一款值得细品的布丁。', /*flavor_3*/'要是你能去看\n我喜欢的书……\n我会很开心的……\n就像这样。', /*disp_order*/25, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/124, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'莉玛的布丁', /*flavor_1*/'好像是伊丽莎白牧场里的家伙。\n好像和牛不太一样。\n摸上去毛茸茸的很舒服，\n所以我在口感上下了功夫。', /*flavor_2*/'这、这是……入口即化！\n瞬间就融化在嘴里！\n这个口感实在令人上瘾！', /*flavor_3*/'让你也变得毛茸茸的吧！', /*disp_order*/26, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/125, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'铃的布丁', /*flavor_1*/'好像是个一天到晚都在睡\n觉的家伙，所以加了很多\n能助眠的药草。不过不知\n道做成布丁会不会好吃。', /*flavor_2*/'唔……很难评价的味道……\n好吃是好吃……\n嗯～一动脑子……\n就想睡觉……', /*flavor_3*/'好麻烦啊～', /*disp_order*/27, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/126, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'菈比莉斯塔的布丁', /*flavor_1*/'这个布丁是可丽饼店里\n一个一身红色的人教我做的，\n教得很仔细认真，\n是个非常温柔的人！', /*flavor_2*/'明……明明是布丁，却有\n可丽饼的味道。味道非常\n普通，但是……感觉要打\n开布丁界的新大门了！', /*flavor_3*/'解放布丁的权能！', /*disp_order*/28, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/127, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'静流的布丁', /*flavor_1*/'这个布丁是根据掉在店里的\n一份谜之菜谱做的。\n菜谱封面写着\n“充满了姐姐的爱情”。', /*flavor_2*/'口感滑嫩，真是极品♪\n吃了之后不知道为什么\n会有种幸福的感觉……\n这就是爱情吗？', /*flavor_3*/'姐姐能量全开！就像这样！', /*disp_order*/29, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/128, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'璃乃的布丁', /*flavor_1*/'是璃乃教我做的！\n不愧是在可丽饼店打工的人，\n看上去很好吃！', /*flavor_2*/'听说吃了之后妹之力？会\n提升。只要能成为妹妹，\n就能随便向哥哥敲诈要布\n丁了！太棒了！', /*flavor_3*/'哥哥，给我布丁！', /*disp_order*/30, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/129, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'日和莉的布丁', /*flavor_1*/'在我为做布丁而烦恼的时候，\n有个路过的大姐姐和我一起\n出了这种布丁。这世上\n偶尔也是有好人的嘛♪', /*flavor_2*/'吃了之后就能打起精神，所以\n宫子为了感谢她提供的菜谱，\n也去和她一起助人为乐了。\n宫子很了不起吧！', /*flavor_3*/'困难的时候就要互相帮助！\n就像这样！', /*disp_order*/31, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/130, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'优衣的布丁', /*flavor_1*/'这是宫子精益求精，努力做\n出来的布丁哦～布丁就是\n治愈。所以宫子温柔\n又认真地做了这个布丁。', /*flavor_2*/'啊……仿佛眼前出现了一片\n花田。有种非常温柔的味道，\n包裹住我的身心。\n这就是…真正的治愈啊♪', /*flavor_3*/'花开治愈♪', /*disp_order*/32, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/131, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'怜的布丁', /*flavor_1*/'为了做出究极的布丁，\n宫子进行了严厉的修行……\n成果就是做出了这个\n冷酷又完美的布丁！', /*flavor_2*/'确实是完美的布丁但要达到\n究极的布丁，我还需要继\n续修行……做布丁真是\n一门高深的学问！', /*flavor_3*/'暴风斩击！就像这样！', /*disp_order*/33, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/132, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'秋乃的布丁', /*flavor_1*/'豪华又高级的\n大小姐布丁，\n里面加了像宝石一样的\n大颗草莓！', /*flavor_2*/'无论是材料还是味道，都是\n精益求精。一流的品质不容\n妥协，应该还能做得更\n豪华、更好吃才对……', /*flavor_3*/'哦呵呵呵！就像这样！', /*disp_order*/34, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/133, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'由加莉的布丁', /*flavor_1*/'要做成外面硬一点，\n中间很嫩滑的布丁，\n还要加入像泡沫一样\n蓬松的生奶油♪', /*flavor_2*/'有人说布丁也是一种饮料，\n试试一口气喝下去……\n松软嫩滑！\n让全身都放松了下来～', /*flavor_3*/'布丁泡沫最棒！就像这样！', /*disp_order*/35, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/134, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'珠希的布丁', /*flavor_1*/'加入红豆泥，\n然后放进烤炉里烤，\n就能做出鲷鱼烧……布丁，\n十分创新！', /*flavor_2*/'这种布丁在刚烤好的时候\n是最好吃的！\n虽然烫，但很好吃，\n我都要忍不住了！', /*flavor_3*/'喵喵喵！就像这样！', /*disp_order*/36, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/135, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'美冬的布丁', /*flavor_1*/'考虑到营养平衡问题，\n加了很多果实，\n在忙于各种事务的时候，\n这种布丁很靠得住哦！', /*flavor_2*/'任何时候都能随便吃也是\n布丁的一种魅力，这种\n布丁饱腹感很强，只吃\n一个都能让你撑很久哦。', /*flavor_3*/'效率全开！就像这样！', /*disp_order*/37, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/136, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'美里的布丁', /*flavor_1*/'用了精灵之森的小孩给我的\n药草做了这个布丁，\n还是第一次尝到味道这么\n温柔的布丁……', /*flavor_2*/'吃了这个布丁，感觉自己就\n能变成一个好孩子。我还把\n最喜欢的布丁送给了一个\n在哭的小孩。感觉不错。', /*flavor_3*/'哎～呀呀♪', /*disp_order*/38, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/137, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'初音的布丁', /*flavor_1*/'午睡的时候在梦里看到了\n这个奇妙的布丁，所以试\n着做了出来。一看到它\n就又想睡觉了……', /*flavor_2*/'甜甜的布丁在嘴里一蹦一蹦\n真是一种未知的味道！感觉我\n现在能用神奇的力量拯救\n世界了！但是好困啊…', /*flavor_3*/'呼噜～', /*disp_order*/39, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/138, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'碧的布丁', /*flavor_1*/'看到店里某个角落里孤零零地\n放着一个玩具，我就想到了这\n个布丁。它和其他布丁放\n在一起，就会瑟瑟发抖。', /*flavor_2*/'不知不觉中\n会藏到角落里。一吃下去，\n宫子就想见忍她们了。', /*flavor_3*/'要从边缘人毕业了！', /*disp_order*/40, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/139, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'望的布丁', /*flavor_1*/'顶级偶像的\n布丁。加了闪闪发亮的\n材料，用很热的烤炉\n烤出来的。', /*flavor_2*/'一股华丽的甜味在嘴里\n扩散开来……这就是偶像吗？\n突然变得想\n唱歌跳舞了！', /*flavor_3*/'加快节奏！', /*disp_order*/41, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/140, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'千歌的布丁', /*flavor_1*/'一个好人的布丁，\n她送了布丁给宫子。\n有在一边呼唤精灵，\n一边努力搅拌材料哦。', /*flavor_2*/'布丁液做得非常顺滑，\n口感满分！\n精灵啊，请为了\n宫子的布丁……', /*flavor_3*/'赐予风之佑护！就像这样！', /*disp_order*/42, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/141, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'纺希的布丁', /*flavor_1*/'会唱歌、会跳舞，还会做衣服\n的那个人的布丁。这个布丁\n包装得很漂亮。能把布丁\n打扮成最棒的样子。', /*flavor_2*/'包装会让布丁\n吃起来更麻烦。但是\n那种麻烦也能\n衬托出布丁的美味。', /*flavor_3*/'我不是为了骑士先生\n才做这些的！就像这样！', /*disp_order*/43, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/142, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'克莉丝提娜的布丁', /*flavor_1*/'除非那家伙给宫子布丁，\n不然对她没兴趣。但是\n因为她在旁边说个不停，\n所以也努力做了这个布丁。', /*flavor_2*/'嗯！？\n布、布丁的口感\n在嘴里疯狂大闹！\n完全控制不住！', /*flavor_3*/'宴会开始了！', /*disp_order*/12, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/143, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'流夏的布丁', /*flavor_1*/'做了一个适合成年人的\n低糖布丁。尝试加了鱼肉\n进去。布丁的可能性\n是无限大的！', /*flavor_2*/'很适合成年人的味道。宫子现\n在也是个受大家信赖的大姐大\n了！宫子是不会放过\n干坏事的人的！', /*flavor_3*/'一刀两断！做好觉悟吧！\n就像这样！', /*disp_order*/44, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/144, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'七七香的布丁', /*flavor_1*/'最可爱最强的魔法布丁。\n内部是五颜六色的～\n甜甜的布丁\n能让人头脑变清晰！', /*flavor_2*/'每吃一口，味道都不一样，\n就像魔法一样！\n说不定吃完之后\n宫子也能变成魔法少女？', /*flavor_3*/'啾！来了！就像这样！', /*disp_order*/45, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/145, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'杏奈的布丁', /*flavor_1*/'一边念一些拗口的话一边做的\n布丁。一种充满魔力的布丁\n就这么诞生了！但总觉得\n有种很尴尬的感觉？', /*flavor_2*/'感觉吃一口就会被谜之组织\n盯上，或者是会想起前世的\n记忆～设定太杂糅了。\n宫子、宫子……', /*flavor_3*/'人称疾风之宫子！', /*disp_order*/46, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/146, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'惠理子的布丁', /*flavor_1*/'宫子把一种酸酸甜甜的红色\n果子切碎之后加进去了\n甜中带着一丝刺激，\n是病娇布丁哦。', /*flavor_2*/'感觉比普通布丁\n更香呢～\n宫子要变得更加\n喜欢布丁了……', /*flavor_3*/'嘻嘻……嘻嘻嘻……', /*disp_order*/47, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/147, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'深月的布丁', /*flavor_1*/'注重健康的宫子\n在布丁里加了药哦～不过\n因为看上去毒性太强，\n所以加了玫瑰来装饰～', /*flavor_2*/'虽然扑鼻的药味有点那个，但\n有种大人的味道，感觉自己都\n要变聪明了呢。有种按捺\n不住想要做实验的冲动。', /*flavor_3*/'无论是什么样的布丁，\n都无法从宫子的独眼中\n逃走！', /*disp_order*/48, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/148, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'莫妮卡的布丁', /*flavor_1*/'小巧但份量十足的\n军人布丁。\n做得相对硬一点，\n加入了很多砂糖。', /*flavor_2*/'嗯～王道布丁的\n甜度和味道就是有保证～\n让人忍不住露出笑容呢。\n嘿嘿……啊！', /*flavor_3*/'我才不是小孩子！就像这样！', /*disp_order*/49, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/149, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'妮侬的布丁', /*flavor_1*/'这个布丁使用了从大江户镇\n收集来的素材，还有在东国\n流传的技巧。咻啪啪！\n这就是忍术·提味之术！', /*flavor_2*/'吃了这个布丁就会有种\n必须侍奉某个人\n的感觉是～也。\n那个人就是将、将……', /*flavor_3*/'将军！和宫子一起\n统一天下是～也！', /*disp_order*/50, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/150, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'空花的布丁', /*flavor_1*/'这个布丁是以你拿来的手铐\n为灵感做出来的，\n为了让布丁不能乱动，\n宫子把它做得很硬哦。', /*flavor_2*/'因为做得很硬，\n所以口感说是布丁，\n又不太像……但这种不上\n不下的感觉也令人上瘾…', /*flavor_3*/'呵呵，呲溜～', /*disp_order*/51, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/151, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'雪的布丁', /*flavor_1*/'非常美丽的布丁呢。\n因为看到什么东西漂亮\n就都加进去，所以结果\n很不得了。', /*flavor_2*/'味道也是，除了美丽再也找不\n到别的形容了。话说，吃了这\n个布丁的宫子也很美呢！\n镜子，镜子在哪里！', /*flavor_3*/'啊，宫子真是太美了～', /*disp_order*/52, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/152, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'步美的布丁', /*flavor_1*/'不知不觉间卖出点心的时候\n想到的一种布丁。\n其实做法也不太记得了……\n但宫子相信一定很好吃！', /*flavor_2*/'嗯，果然很好吃……\n嗯？那家伙\n就在那里。奇怪？\n身体擅自走向那家伙……', /*flavor_3*/'盯着看……不对，\n宫子在干什么，\n宫子才不是跟踪狂！', /*disp_order*/53, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/153, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'未奏希的布丁', /*flavor_1*/'这个布丁是和那个喜欢\n恶作剧的手下一起做的。虽然\n看上去很可爱，但做法\n出人意料的复杂哦～', /*flavor_2*/'味道和外表一样，甜蜜又温柔\n但菜谱背面还写着\n别的做法哦。照这个来做\n就会做出超辣布丁……', /*flavor_3*/'诶嘿嘿，到恶作剧的时间了！', /*disp_order*/54, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/154, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'美美的布丁', /*flavor_1*/'这是和乖巧又温柔的手下\n一起做的，\n摆了很多动物的玩偶。', /*flavor_2*/'嗯～又松软又香甜！\n太开心了，\n感觉身心都要变得\n噜嗒嗒了。', /*flavor_3*/'跳啊跳啊跳♪', /*disp_order*/55, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/155, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'镜华的布丁', /*flavor_1*/'这个布丁是和害怕幽灵的手下\n一起做的。\n小小的，却做得很好，\n是个非常可爱的布丁。', /*flavor_2*/'吃起来凉凉的，在热天\n会格外好吃哦！\n嗯？有大人想买\n这个？这不是……', /*flavor_3*/'变态可疑分子吗！', /*disp_order*/56, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/156, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'铃奈的布丁', /*flavor_1*/'精英模特一样的布丁。\n加了大量装饰\n来表现非凡的魅力。', /*flavor_2*/'视觉和味觉两方面都能给人\n享受的新型布丁。宫子虽然是\n幽灵，但现在也想用自己\n的双脚去走台步了！', /*flavor_3*/'瞄准你的心！就像这样！', /*disp_order*/57, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/157, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'伊绪的布丁', /*flavor_1*/'这是那个在学校当老师的人\n的布丁。本来想做得硬一点，\n不知不觉间变得性感了。\n到底是为什么呢？', /*flavor_2*/'虽、虽然所有布丁都是属于\n宫子的，但这个布丁有种不可\n思议的味道，让宫子想让\n更多人了解！', /*flavor_3*/'开始授课咯♪', /*disp_order*/58, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/158, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'美咲的布丁', /*flavor_1*/'这个布丁的灵感来源是喜欢\n装大人的早熟小孩。有意控制\n了甜度。但可以另外加焦\n糖，所以大可放心哦。', /*flavor_2*/'只要是布丁，不管是什么味道\n宫子都可以吃。但焦糖\n还是要加一点点的。\n只加一点点的话……', /*flavor_3*/'也是一个优秀的淑女！', /*disp_order*/59, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/159, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'帆稀的布丁', /*flavor_1*/'做了一个充满谜团的布丁。\n好像隐藏着什么……\n这真是个危险的布丁啊！', /*flavor_2*/'味道非常豪华！\n有种首领的风味！\n向伟大的宫子\n献上布丁吧！', /*flavor_3*/'要是不拿布丁来，\n就要惩罚你了哦☆', /*disp_order*/60, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/160, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'嘉夜的布丁', /*flavor_1*/'造型很狂野！\n看来很值得期待啊。\n味道一定充满了跃动感！', /*flavor_2*/'第一口\n非常有冲击性！\n突然特别想和人打架！\n把布丁给我交出来！', /*flavor_3*/'是热血沸腾的感觉！', /*disp_order*/61, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/161, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'祈梨的布丁', /*flavor_1*/'感觉卖相不怎么起眼啊，\n这个真的没问题吗？\n感觉不太可靠……', /*flavor_2*/'虽然外表不太行，但味道很不\n错……还有个性也很鲜明！\n光说干劲和自信的话\n还是有的，唔哦哦哦！', /*flavor_3*/'以下克上，一定要成功！', /*disp_order*/62, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/162, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'矛依未的布丁', /*flavor_1*/'这是天下闻名的大恶人的布\n丁。宫子抛弃了以往的常识，\n尝试做了这个布丁～', /*flavor_2*/'唔唔唔……吃了之后\n特别有干劲！\n胆敢妨碍宫子，\n就把他们一起打飞～', /*flavor_3*/'给我让开！就像这样！', /*disp_order*/63, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/163, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'似似花的布丁', /*flavor_1*/'是那个能变身成不同模样，\n还能分身的家伙\n的布丁。我用模具\n做了很多很多同样的布丁。', /*flavor_2*/'不知不觉间就\n全都吃光了。为什么\n布丁不能分身呢？\n给我无限增殖啊！', /*flavor_3*/'魔镜魔镜！就像这样！', /*disp_order*/64, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/164, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'克蕾琪塔的布丁', /*flavor_1*/'是守财奴的布丁。我尝试用\n金钱的力量做了这个\n布丁。和奶奶的布丁\n是两个极端。', /*flavor_2*/'唔…虽然不想承认，但很\n好吃……要赚到足够的钱做好\n几个这样的布丁的话会很\n辛苦…但真的很好吃…', /*flavor_3*/'我向布丁起誓，\n一生效忠于布丁！\n就像这样！', /*disp_order*/65, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/165, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/'花凛的布丁', /*flavor_1*/'是那个给大家提供了很多便利\n的公会管理协会职员的布丁。\n为了营造行政人员的感觉\n用了正统、保险的做法。', /*flavor_2*/'保险！非常保险！\n是一种毫无破绽的布丁！\n现在的宫子应该能\n完美地撰写文件材料了！', /*flavor_3*/'欢迎来到公会管理协会！\n就像这样！', /*disp_order*/66, /*init_flg*/1);
