INSERT INTO `room_unit_comments` VALUES (/*id*/6166, /*unit_id*/121801, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/'不知不觉我已经按照\n秋乃小姐的步调走了……\n奇妙的是，\n我并不讨厌这样。', /*insert_word_type*/0);
