INSERT INTO `room_unit_comments` VALUES (/*id*/6172, /*unit_id*/121801, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/'我说啊，你和秋乃小姐……\n不，还是算了……', /*insert_word_type*/0);
