INSERT INTO `room_unit_comments` VALUES (/*id*/6174, /*unit_id*/121801, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/'早起三分利，\n为了迎接美好的一天，\n该起床了。', /*insert_word_type*/0);
