INSERT INTO `room_unit_comments` VALUES (/*id*/6178, /*unit_id*/121801, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'嗯……\n真让人心情平静。\n感觉快要睡着了……', /*insert_word_type*/0);
