INSERT INTO `room_unit_comments` VALUES (/*id*/6192, /*unit_id*/122001, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/'和那个人一起欣赏的\n海景……我想永远\n铭记在心。', /*insert_word_type*/0);
