INSERT INTO `room_unit_comments` VALUES (/*id*/6194, /*unit_id*/122001, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'我的划船技术进步了，\n下次让你看看。', /*insert_word_type*/0);
