INSERT INTO `room_unit_comments` VALUES (/*id*/6224, /*unit_id*/121901, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'我可是传说中的海盗，\n当然也很擅长游泳！\n就、就是有点怕太深的地方……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6225, /*unit_id*/121901, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/'呵呵，为了漂流到\n无人岛的时候不慌，\n我已经准备好了应急食品。\n也有吉格鲁特的份哦！', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6226, /*unit_id*/121901, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/'冥风战记的\n新章节完成了！\n吉格鲁特，\n我希望你第一个看！', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6227, /*unit_id*/121901, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/'呵呵，天气晴朗，\n是个很适合航海的日子！\n我们赶快出海吧！', /*insert_word_type*/0);
