INSERT INTO `room_unit_comments` VALUES (/*id*/6231, /*unit_id*/121901, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'真是一件好东西，\n适合大怪盗用来放松休闲！\n那个……在哪家店\n可以买到？', /*insert_word_type*/0);
