INSERT INTO `room_unit_comments` VALUES (/*id*/6234, /*unit_id*/121901, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'这就是水手们口耳相传的，\n在大海上徘徊的神秘火焰吗！\n好暖和！', /*insert_word_type*/0);
