INSERT INTO `room_unit_comments` VALUES (/*id*/6744, /*unit_id*/106901, /*trigger*/3, /*voice_id*/8, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'无聊的书\n就由我来重写。\n这样写可以吗？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6746, /*unit_id*/124001, /*trigger*/1, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'今年由茜里和姐姐\n两个人来扮圣诞老人……\n也就是说，幸福感\n也翻倍了呢♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6747, /*unit_id*/124001, /*trigger*/1, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'为了圣诞节做准备，\n要练习萨克斯……\n茜里会和哥哥一起\n拼命特训的！', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6748, /*unit_id*/124001, /*trigger*/1, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'圣诞树装饰得\n非常完美♪\n哥哥姐姐会喜欢吗？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6749, /*unit_id*/124001, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'哇，外面一片雪白！\n就像一块满是\n鲜奶油的蛋糕♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6750, /*unit_id*/124001, /*trigger*/1, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'从烟囱里进去\n会弄脏衣服的……\n就不能走大门进去吗？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6751, /*unit_id*/124001, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/'等哥哥来了，\n要让他多夸夸\n我这身圣诞装扮♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6752, /*unit_id*/124001, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/'如果能和哥哥一起\n度过圣诞夜……\n茜里或许也能变得\n稍微成熟一点呢♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6753, /*unit_id*/124001, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'茜里现在是\n圣诞老人♪\n可以许下\n任何愿望哦？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6754, /*unit_id*/124001, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'外面很冷，\n就和茜里\n依偎着出门吧♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6755, /*unit_id*/124001, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'因为哥哥是\n茜里的驯鹿，\n所以请永远\n陪在茜里身边♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6756, /*unit_id*/124001, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'哥哥，救命！\n茜里练习把自己\n打包成礼物，\n结果被缠住了……', /*insert_word_type*/0);
