INSERT INTO `room_unit_comments` VALUES (/*id*/6758, /*unit_id*/124001, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/'茜里希望能收到\n比较成熟的礼物呢。\n因为想要早点追上哥哥♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6759, /*unit_id*/124001, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/'早上好♪呵呵，\n茜里圣诞老人是\n好孩子的榜样，\n所以会早睡早起♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6760, /*unit_id*/124001, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/'哎呀，已经中午了吗？\n一直在练习\n圣诞乐曲，居然已经\n这个时间了……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6761, /*unit_id*/124001, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/'冬夜里，天空中的\n星星好美呢～\n哥哥，要不要一起\n去看看？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6763, /*unit_id*/124001, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'啊，太舒服了，\n感觉都要融化了……\n这样的礼物\n似乎也不错呢……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6764, /*unit_id*/124001, /*trigger*/3, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'哇，好可爱♪\n诶嘿嘿，哥哥想要\n什么样的礼物呢？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6765, /*unit_id*/124001, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'哇～有一种成熟的香气……\n看来能度过一个\n浪漫的圣诞节♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6766, /*unit_id*/124001, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'用这个来温暖\n茜里的身体，然后\n茜里再……把这份温暖\n分享给哥哥吧♪', /*insert_word_type*/0);
