INSERT INTO `room_unit_comments` VALUES (/*id*/6912, /*unit_id*/124601, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/'正如和他一起\n在绘马上写的那样，\n希望今年大家也能\n在欢笑中度过。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6913, /*unit_id*/124601, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'和老师一起去\n参加新年参拜吧♪\n为了不迷路，\n要牵着老师的手哦？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6914, /*unit_id*/124601, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'新年大促销真不错呢。\n但是一不小心就会\n这也想买、那也想买，\n最终买太多……', /*insert_word_type*/0);
