INSERT INTO `room_unit_comments` VALUES (/*id*/7131, /*unit_id*/120001, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'这种香味……\n和弟弟身上的一样……\n嗅嗅、嗅嗅…', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7132, /*unit_id*/120001, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'来暖暖身子吧……\n我会将这份热量\n全都传递给弟弟哦！', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7134, /*unit_id*/120001, /*trigger*/3, /*voice_id*/7, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'要召唤迷你弟弟\n就得多吃点东西……\n因为使用魔法\n会让肚子变饿。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7135, /*unit_id*/120001, /*trigger*/3, /*voice_id*/8, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'守护弟弟的和平生活……\n为此，我要到处检查♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7137, /*unit_id*/125301, /*trigger*/1, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'起初还不太习惯\n穿这件衣服，但现在\n已经非常适应它\n穿在身上的感觉了。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7138, /*unit_id*/125301, /*trigger*/1, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'想要守护自然，\n就得努力学习。\n每天都要不断进步。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7139, /*unit_id*/125301, /*trigger*/1, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'把采集到的药草\n调配好吧，毕竟不知道\n什么时候会用得上。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7140, /*unit_id*/125301, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'集训虽然很艰苦，\n但多亏了集训，\n我好像变得\n更有力气了♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7141, /*unit_id*/125301, /*trigger*/1, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'到了给小狗狗主人\n喂饭的时间了呢……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7142, /*unit_id*/125301, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/'为了能更好地\n为主人服务，\n我得更加努力地\n训练和学习……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7143, /*unit_id*/125301, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/'哎呀，差不多该带\n小狗狗主人\n出去散步了。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7144, /*unit_id*/125301, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'主人，如果方便的话，\n要不要一起去散步呢？\n今天森林里很平静，\n应该会很惬意。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7145, /*unit_id*/125301, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'小狗狗主人现在也是\n【美食殿堂】的重要伙伴。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7146, /*unit_id*/125301, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'森林里充满了危险，\n请务必小心，\n不要受伤了。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7147, /*unit_id*/125301, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'如果对森林或动物\n有任何疑问，\n请随时问我。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7148, /*unit_id*/125301, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/'在进入森林之前，\n请务必检查一下装备，\n约好了哦，主人？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7149, /*unit_id*/125301, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/'在森林里与主人一起\n悠闲度过的时光，\n对我来说\n是非常珍贵的时刻。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7150, /*unit_id*/125301, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/'早上好，\n好好吃早餐，\n为一天的工作\n做好准备吧。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7151, /*unit_id*/125301, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/'到了给小狗狗主人\n喂零食的时间了，\n来，请吃吧。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7152, /*unit_id*/125301, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/'无论多么习惯露营的\n生活，如果有条件，\n我还是愿意选择\n在床上休息。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7154, /*unit_id*/125301, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'就是这个。\n小狗狗主人\n似乎也很喜欢它呢。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7155, /*unit_id*/125301, /*trigger*/3, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'是不是也应该\n把这样的玩具\n给小狗狗主人玩呢？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7156, /*unit_id*/125301, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'这香味真是怡人，\n下次也试试\n天然的香料吧。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7157, /*unit_id*/125301, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'露营的时候，取暖\n是非常重要的事呢。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7159, /*unit_id*/125301, /*trigger*/3, /*voice_id*/7, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'有食物的时候就吃，\n要在大自然中生存时，\n这一点也很重要。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7160, /*unit_id*/125301, /*trigger*/3, /*voice_id*/8, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'让我们积累更多的\n知识，充分利用自然的\n力量吧。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7162, /*unit_id*/100901, /*trigger*/1, /*voice_id*/103, /*beloved_step*/12, /*time*/0, /*face_id*/1, /*description*/'这次的《冥风战记》\n也写得相当不错哦。\n呵呵，吉格鲁特\n也会喜欢吗？', /*insert_word_type*/0);
