INSERT INTO `room_unit_comments` VALUES (/*id*/7165, /*unit_id*/125401, /*trigger*/1, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'呵呵……\n这就是\n游骑兵的制服吗。', /*insert_word_type*/0);
