INSERT INTO `room_unit_comments` VALUES (/*id*/7167, /*unit_id*/125401, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'我想更了解魔物，\n去找找相关的书籍吧。', /*insert_word_type*/0);
