INSERT INTO `room_unit_comments` VALUES (/*id*/7172, /*unit_id*/125401, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'如果能再次和你一起\n做游骑兵的工作，\n那我会很开心的。', /*insert_word_type*/0);
