INSERT INTO `room_unit_comments` VALUES (/*id*/7176, /*unit_id*/125401, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/'能和你并肩\n做游骑兵的工作，\n我很开心……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7177, /*unit_id*/125401, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/'早安，\n游骑兵·栞\n精神饱满地出勤了。', /*insert_word_type*/0);
