INSERT INTO `room_unit_comments` VALUES (/*id*/7179, /*unit_id*/125401, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/'明天还要工作，\n必须得早起。\n晚安。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7181, /*unit_id*/125401, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'呼……一直在\n为了工作东奔西跑，\n我要稍微休息一下。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7182, /*unit_id*/125401, /*trigger*/3, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'工作结束后，\n像这样摸摸它，\n心情就能得到放松呢……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7183, /*unit_id*/125401, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'有种森林的香气……\n今天应该能\n睡个好觉……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7184, /*unit_id*/125401, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'和小喵特\n一样暖和……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7186, /*unit_id*/125401, /*trigger*/3, /*voice_id*/7, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'做着游骑兵的工作，\n肚子就会饿。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7187, /*unit_id*/125401, /*trigger*/3, /*voice_id*/8, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'无论多么忙碌，\n唯有读书\n绝对不能放弃。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7189, /*unit_id*/125801, /*trigger*/1, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'兄长大人不在的时候，\n我会守护好\n兄长大人的家！', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7190, /*unit_id*/125801, /*trigger*/1, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'既、既、既然受邀来到\n兄长大人的家里……\n就不能失礼，必须要\n表现得更得体一些！', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7191, /*unit_id*/125801, /*trigger*/1, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'呵呵……兄长大人的\n美好品性，从整个屋子中\n都散发出来了♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7192, /*unit_id*/125801, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'我不习惯没脱鞋\n就进入他人家中……\n啊！飞进去不就好了！', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7193, /*unit_id*/125801, /*trigger*/1, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'如果在这里插上花，\n应该会很好看呢。\n兄长大人……\n喜欢花吗？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7194, /*unit_id*/125801, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/'我想永远待在这里……\n如果说出这样的话，\n应该会让兄长大人\n为难吧……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7195, /*unit_id*/125801, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/'要是能做些料理\n迎接兄长大人就好了……\n必须提高厨艺才行了！', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7196, /*unit_id*/125801, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'请、请原谅我的失态！\n因、因为有老鼠，\n就不由得认真起来了！', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7197, /*unit_id*/125801, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'欢迎回来。\n请让我为您\n缓解一下\n身体的疲劳吧。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7198, /*unit_id*/125801, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'陪兄长大人聊天！？\n怎、怎么办呢……\n我没什么有趣的\n话题可说……唔……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7199, /*unit_id*/125801, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'不知怎么的，\n我很招动物喜欢……\n在寻找迷路猫咪的\n兼职工能派上用场吗？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7200, /*unit_id*/125801, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/'璃、璃亚小姐说，\n到兄长大人家里后\n就要“陪睡”，这是一种礼节……\n这是……真的吗？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7201, /*unit_id*/125801, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/'我可以……\n留在这里吧？\n作为你的“同伴”……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7202, /*unit_id*/125801, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/'早起是我的强项，\n如果兄长大人起不来，\n我可以帮忙叫醒你哦。', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7203, /*unit_id*/125801, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/'兄长大人……\n我尝试做午餐，\n但是失败了……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7204, /*unit_id*/125801, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/'呼啊……啊！\n让……让兄长大人\n看到我丢脸的一面了！', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7206, /*unit_id*/125801, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'这、这可不行……！\n明明是在别人家里，\n我却不小心……\n放松下来了！', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7207, /*unit_id*/125801, /*trigger*/3, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'它、它不会\n扑过来的……对吧？', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7208, /*unit_id*/125801, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'吸……呼……\n这股芳香净化了\n我的心灵……', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7209, /*unit_id*/125801, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'好温暖……\n就像是兄长大人\n温柔又宽广的胸怀一样♪', /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7211, /*unit_id*/125801, /*trigger*/3, /*voice_id*/7, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/'吃这么多……\n会不会很失态？', /*insert_word_type*/0);
