INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1002001, /*where_type*/1002, /*character_id*/190801, /*motion_type*/12, /*description*/'点击活动的爱心！\n帮助静流小姐和惠理子小姐\n制作\n巧克力蛋糕吧！', /*voice_id*/0, /*start_time*/'2020-01-01 0:00:00', /*end_time*/'2099-12-31 23:59:59');
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1003001, /*where_type*/1003, /*character_id*/190801, /*motion_type*/11, /*description*/'拽着空花\n飞向更远的地方吧！\n用各种障碍让空花\n心醉神迷！', /*voice_id*/0, /*start_time*/'2020-01-01 0:00:00', /*end_time*/'2099-12-31 23:59:59');
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1004001, /*where_type*/1004, /*character_id*/190801, /*motion_type*/3, /*description*/'点击左右两边，击倒\n奄美魔物吧！\n看招！\n琉球犬大回旋踢击！', /*voice_id*/0, /*start_time*/'2020-01-01 0:00:00', /*end_time*/'2099-12-31 23:59:59');
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1005001, /*where_type*/1005, /*character_id*/190801, /*motion_type*/7, /*description*/'确定要点击屏幕\n和龙族据点的各位一起\n进行接龙对决吗？', /*voice_id*/0, /*start_time*/'2020-01-01 0:00:00', /*end_time*/'2099-12-31 23:59:59');
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1006001, /*where_type*/1006, /*character_id*/190801, /*motion_type*/2, /*description*/'点击屏幕击退那些\n迅速移动的老鼠吧！\n和干劲十足的铃一起\n保护重要的芝士吧！', /*voice_id*/0, /*start_time*/'2022-05-31 11:00:00', /*end_time*/'2099-12-31 23:59:59');
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1007001, /*where_type*/1007, /*character_id*/190801, /*motion_type*/4, /*description*/'和美里还有各位部员\n一起进行击球特训吧！\n看准时机把球打回去吧，\n目标是全垒打', /*voice_id*/0, /*start_time*/'2022-08-15 11:00:00', /*end_time*/'2099-12-31 23:59:59');
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1008001, /*where_type*/1008, /*character_id*/190801, /*motion_type*/6, /*description*/'使用慈乐之音奖章，\n获取应援周边吧！\n可以使用金币\n来兑换奖章哦。\n', /*voice_id*/0, /*start_time*/'2020-01-01 0:00:00', /*end_time*/'2099-12-31 23:59:59');
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1009001, /*where_type*/1009, /*character_id*/190801, /*motion_type*/13, /*description*/'吃过的布丁就在\n布丁笔记中确认吧！\n布丁的材料\n可以用金币来购买。', /*voice_id*/0, /*start_time*/'2020-01-01 0:00:00', /*end_time*/'2099-12-31 23:59:59');
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1010001, /*where_type*/1010, /*character_id*/190801, /*motion_type*/4, /*description*/'通过团队合作来挑战谜题吧！\n是要单独测试自己的实力，\n还是要和伙伴们组队\n游玩，请选择喜欢的模式吧♪', /*voice_id*/0, /*start_time*/'2020-01-01 0:00:00', /*end_time*/'2099-12-31 23:59:59');
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1011001, /*where_type*/1011, /*character_id*/190801, /*motion_type*/6, /*description*/'３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪', /*voice_id*/0, /*start_time*/'2022-07-18 15:00:00', /*end_time*/'2030-04-01 14:00:00');
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1012001, /*where_type*/1012, /*character_id*/190801, /*motion_type*/6, /*description*/'キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪', /*voice_id*/0, /*start_time*/'2023-09-18 15:00:00', /*end_time*/'2030-04-01 14:00:00');
