INSERT INTO `srt_panel` VALUES (/*reading_id*/1000400, /*reading*/'菠萝', /*read_type*/1, /*panel_id*/10004, /*detail_text*/'生长在温暖地区，外表呈黄色的酸酸甜甜的水果。', /*head_symbol*/'o', /*tail_symbol*/'o');
