INSERT INTO `srt_panel` VALUES (/*reading_id*/1001100, /*reading*/'眼镜', /*read_type*/1, /*panel_id*/10011, /*detail_text*/'佩戴在眼睛前，用于矫正视力的道具。', /*head_symbol*/'an', /*tail_symbol*/'ing');
INSERT INTO `srt_panel` VALUES (/*reading_id*/1001101, /*reading*/'镜片', /*read_type*/2, /*panel_id*/10011, /*detail_text*/'可用在眼镜和各种东西上，能折射光线的透明物体。', /*head_symbol*/'ing', /*tail_symbol*/'an');
