INSERT INTO `srt_panel` VALUES (/*reading_id*/1001205, /*reading*/'侦探放大镜', /*read_type*/3, /*panel_id*/10012, /*detail_text*/'香澄的专用装备。她跟这放大镜共同经历了数个案件。', /*head_symbol*/'en', /*tail_symbol*/'ing');
INSERT INTO `srt_panel` VALUES (/*reading_id*/1001300, /*reading*/'烤肉', /*read_type*/1, /*panel_id*/10013, /*detail_text*/'烤熟后会变得鲜嫩多汁的美味食材。', /*head_symbol*/'ao', /*tail_symbol*/'ou');
