INSERT INTO `srt_panel` VALUES (/*reading_id*/1001500, /*reading*/'蝙蝠', /*read_type*/1, /*panel_id*/10015, /*detail_text*/'为数不多能够飞翔的哺乳动物。虽然在人们的印象里，它们会吸血，但真正吸血的品种只是极少数。', /*head_symbol*/'an', /*tail_symbol*/'u');
