INSERT INTO `ttk_navi_comment` VALUES (/*comment_id*/24007, /*where_type*/24, /*character_id*/102601, /*face_type*/2, /*character_name*/'铃', /*description*/'没想到\n我也挺能干的嘛？', /*voice_id*/6, /*start_time*/'2022/04/30 11:00:00', /*end_time*/'2099/12/31 23:59:59', /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10040);
