INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130041, /*equipment_name*/'捣蛋礼物', /*description*/'未奏希骄傲地将这个恶作剧盒子称为她的最高杰作。可不能把它当成是小孩子的玩具，其视觉效果和冲击的声音一定能让敌人吓破胆。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/67.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
