INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130521, /*equipment_name*/'野心礼服', /*description*/'我想要穿上这条裙子！这条特别定制的长裙承载着莉玛的梦想。怀梦少女的妄想力，此刻觉醒。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/25.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130531, /*equipment_name*/'羽翼大衣', /*description*/'莫妮卡穿着的大衣。奢华的剪裁能刺激追随者们的身心，大幅提升其所有类型的攻击力。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/52.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/4.0, /*physical_critical*/4.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130541, /*equipment_name*/'高级定制皮带', /*description*/'纺希穿戴的能容纳线轴的特制随身皮带。能迅速从线轴中抽出魔法丝线，束缚敌人，在一瞬间剥夺敌人的意识。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/4.0, /*physical_critical*/4.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130551, /*equipment_name*/'跟踪用斗篷', /*description*/'步美亲手制作的斗篷，制作时花费了一番功夫，穿上它就能让别人察觉不到自己的存在。能在不知不觉间靠近对方，更加接近喜欢的前辈。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/14.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130561, /*equipment_name*/'真打 绝海·波浪丸', /*description*/'流夏佩戴的双刀。据说一旦在恶徒们面前抽出第二把刀·波浪丸，马上就能平息纷争。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130571, /*equipment_name*/'亚修凯隆', /*description*/'立志追求极致剑道的姬塔拿在手上的单手剑。她在寂灭世界中自由自在地持续挥舞着手中的剑，不知不觉中就被人称为剑圣。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/90.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/22.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/1.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130581, /*equipment_name*/'公主之剑', /*description*/'佩可莉姆所使用的『皇家装备』的真正形态。本就拥有很强的力量，而今因沉睡之力被释放，更是被赋予了愈加强大的力量。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130591, /*equipment_name*/'爱梅斯守护符', /*description*/'可可萝佩戴的花饰。受到了被尊为神明的伟大存在的守护，花朵中飘荡出的温柔花香，赋予人战斗的勇气。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130601, /*equipment_name*/'混沌魔法书', /*description*/'凯露随身携带的魔法书。通过控制快要从书中喷涌而出的魔力便可以自由自在地操纵强大的招数。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/94.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130611, /*equipment_name*/'情谊的证明', /*description*/'矛依未贴身携带的\n胸针。她的搭档曾经将此托付给她，\n她们之间的情谊与温暖，带给她\n挥动天楼霸断剑的力量。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/99.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/9.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/1.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130631, /*equipment_name*/'女王缇达尼亚斯', /*description*/'亚里莎惯用的巨弓。是在精灵之间流传的传说之弓，仅凭拉弓就可封住目标的动作，所释放的箭矢能射穿任何东西。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/95.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130651, /*equipment_name*/'龙之拳', /*description*/'嘉夜身上穿戴着的如同巨龙臂膀般的\n手甲。传说会根据佩戴者的激情\n喷射出业火，将敌对之人\n全部燃烧殆尽。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/96.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/31.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130711, /*equipment_name*/'圣域剑阿瓦隆', /*description*/'克莉丝提娜挥舞着的大剑。经过精准的计算，剑尖所到之处皆无人可逃，都会被一刀两断，必定能获得胜利。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130751, /*equipment_name*/'伞剑沙滩公主', /*description*/'佩可莉姆常用的充满夏日风情的太阳伞利剑。她脸上绽放的笑容配合华丽而可爱的剑技，必定能吸引沙滩上所有人的目光。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/25.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/1.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130761, /*equipment_name*/'凉枪水精灵果汁', /*description*/'可可萝的矛，充满了夏日风情。清凉澄澈的剑尖挥舞出凉爽的蓝色轨迹，不管是狂暴的魔物还是酷暑，都能瞬间冷却下来。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/42.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130771, /*equipment_name*/'太阳魔杖 闪耀夏日', /*description*/'铃莓在夏天使用的新魔杖。华丽的装饰看起来很休闲，而且不管怎么摔，上面的芭菲也不会掉下来。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/40.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130781, /*equipment_name*/'夏日猫猫泳圈', /*description*/'凯露为了享受夏日而偷偷准备的泳圈。虽然本人说是看中了安全性才买的，不过这设计实在太过可爱，让人怀疑她话中的真假。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/85.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/15.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130791, /*equipment_name*/'鲷鱼棒冰匕首', /*description*/'珠希为了度过酷暑而随身携带的棒冰。鲷鱼烧冰冻后的滋味让珠希步履轻盈，将敌人玩弄于股掌之间。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/30.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/1.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130801, /*equipment_name*/'苍龙矛 蛟龙', /*description*/'美冬为有效避暑而准备的矛。它击出的水流宛如狂暴海神一般，能高效地打倒敌人。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/86.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130811, /*equipment_name*/'南瓜灵镰', /*description*/'骷髅老爸为忍准备的灵镰。其中蕴含着万圣节的加护，南瓜灯能将灵魂引导至冥界，其刀刃能撕裂恶灵。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/35.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/1.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130821, /*equipment_name*/'万圣节布丁', /*description*/'充满了万圣节风情的布丁，点心师也为之感到骄傲。其中有着微微的南瓜甜味，让品尝过世间所有布丁的宫子也惊喜万分。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/66.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130831, /*equipment_name*/'魔法扫帚·万圣节之眼', /*description*/'被美咲打扮得很有万圣节风格的大眼仔魔杖。成人的暴力美感和万圣节的可爱风格融合得十分完美。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/60.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/1.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130841, /*equipment_name*/'符文之雪翠灵杖', /*description*/'它是千歌的魔法杖，有圣夜的精灵寄宿其中。精灵们会回应唱唤术，赋予圣诞节的加护，大幅提升受加护对象的能力。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/80.0, /*atk*/0.0, /*magic_str*/46.0, /*def*/5.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130851, /*equipment_name*/'圣诞装饰铃铛', /*description*/'受到圣诞节祝福的胡桃常用的铃铛。胡桃奏响铃铛时寄托的大无畏精神能唤起同伴们的勇气，让她们绝不气馁。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/125.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130861, /*equipment_name*/'圣诞王噗吉', /*description*/'充满圣诞色彩的绫音的搭档。噗吉更加可爱的同时，也提升了绫音的力量，让她能发出更强的攻击。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/50.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130871, /*equipment_name*/'猫明神喵拳', /*description*/'在一个跟猫咪有关的神社里供奉的来历正统的护手。能为穿戴者带来胜利，与日和莉正直的心相呼应，为她带来极大的庇佑。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/86.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/1.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130881, /*equipment_name*/'拂晓之祝杖 花束', /*description*/'在迎新年的庆典上使用的神具之杖。只要怀着祝福大家的心愿挥舞，就会有花朵散落，为新一年的旅途增添色彩。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/50.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/30.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/1.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130891, /*equipment_name*/'冰丽刀·初华', /*description*/'在新年迎接神明的祭祀上使用的最好的刀。刀身美丽，宛如坚冰。不论是深沉的黑暗还是不详的烦恼都能一刀两断。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/24.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/13.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130901, /*equipment_name*/'毁灭打蛋器', /*description*/'惠理子为给真命天子做巧克力而准备的只有她自己能驾驭的烘焙用具。饱含爱意的一击能将一切破坏殆尽。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/25.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130911, /*equipment_name*/'装饰之心', /*description*/'静流为了给弟弟做好吃的巧克力而戴上的帽子。心形装饰会散发甜甜的香气，给予同伴关爱，让同伴安心。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/100.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130921, /*equipment_name*/'安的魔导书', /*description*/'安爱用的魔导书。能够最大限度地\n激发出使用罕见魔法的\n才能。公主想要引导国家的决意\n让英灵的力量变得更加强大。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/88.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/1.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130931, /*equipment_name*/'小眼睛！', /*description*/'露的搭档小眼睛。不知为何，即使在\n不熟悉的异世界中也会被召唤，像道\n具一样被利用，这大概……是因为有\n着信赖关系存在吧。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/70.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/35.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130941, /*equipment_name*/'龙之护身符', /*description*/'能够提升古蕾雅能力的\n龙之护身符。在宝石的加护下，\n龙族公主的火焰更加猛烈，\n将一切敌人燃烧殆尽。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/92.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130951, /*equipment_name*/'万紫千红艳椿', /*description*/'让空花心潮澎湃的妖艳和服。\n带子的松紧程度加剧了她的兴奋\n，当将其松绑时，未知的愉悦\n也会随着旋转而释放出来。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/12.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130961, /*equipment_name*/'变身忍者套装', /*description*/'妮侬在大江户镇上买的\n忍者变身装备。武器本身没有\n特别的力量，但不知为何，\n妮侬拿着它就会觉醒为女忍者。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/85.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/40.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130971, /*equipment_name*/'雷姆的发饰', /*description*/'雷姆经常戴着的发饰。\n和最喜欢的姐姐同款的发饰，\n似乎很喜欢与姐姐头发颜色一样的\n红色绳子工艺。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/33.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/40.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/15.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130981, /*equipment_name*/'拉姆的发饰', /*description*/'拉姆经常戴着的发饰。\n对她而言，这是她作为女仆的证明，\n似乎很喜欢与妹妹头发颜色一样的\n蓝色绳子工艺。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/52.0, /*def*/8.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/1.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130991, /*equipment_name*/'约定的花束', /*description*/'爱蜜莉雅很重视的花束。如同\n雪花飘落般梦幻的花瓣\n充满了玛那，\n深受精灵们的喜爱。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/96.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/6.0, /*energy_recovery_rate*/1.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131001, /*equipment_name*/'盛夏弓爱心飞溅', /*description*/'铃奈为了夏日的工作而新制作的\n弓。清凉的设计和配色让人联想\n到盛夏海边，这正是铃奈讲究的\n地方。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/96.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/1.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131011, /*equipment_name*/'惠爱剑夏日爱心家族', /*description*/'在夏日的海边，伊绪为了守护学生\n而挥舞的剑。为自己学生着想的心\n转变成治愈的力量。在这个夏天\n也一定会大有作为。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/80.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/6.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131031, /*equipment_name*/'纤丽剑夏日闪耀', /*description*/'咲恋在夏天携带着的剑。其中寄托着\n孩子们的愿望，希望她能够作为一位\n女性，而并非救济院的妈妈，\n好好享受夏日。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/30.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131041, /*equipment_name*/'夏日狼人之剑', /*description*/'一把由海狼的獠牙铸造的大剑，\n海狼据说只会出现在特定且平静海面\n上。真琴非常中意上面的狼的装饰。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/88.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/1.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131051, /*equipment_name*/'琉球犬式·神武之护手', /*description*/'为了向被称为海神的大海献上舞蹈而\n存在的神具护手。布的花纹中倾注了\n许多对大海的感激之情，戴着这副护\n手起舞的香织展现出大海的浩瀚。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/90.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/28.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131061, /*equipment_name*/'夏华杖奇幻传说', /*description*/'真步公主精心制作的沙滩风格的魔法\n杖。其中封印着夏日的光景，每挥舞\n一次，就会在灯火的照耀下显现出影\n画，同时夜空中绽放出巨大的烟花。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/78.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/18.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/1.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131071, /*equipment_name*/'好朋友的弓琴', /*description*/'在插班进入圣特蕾莎女子学院时，\n碧为了克服孤独而新制作的最终\n兵器。虽然原本是用来充当守护符，\n但现在也成为了友情的证明。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/91.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/24.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131081, /*equipment_name*/'黄昏至黎明之刃', /*description*/'用于料理、裁缝、帮弟弟们画图等，\n用途多样的克罗依的万能日常道具。\n在她如此主张下，面对教导主任检查\n随身物品时，有着绝对的防御力。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/77.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/35.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131091, /*equipment_name*/'桃色☆荧光棒☆指虎', /*description*/'千爱瑠为了追求可爱和强大，亲手制作的\n快乐切噜指虎。有需要时可以\n通过折断荧光粉色的指甲来发动\n发光魔法，为憧憬的歌姬应援。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/79.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/40.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/1.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131101, /*equipment_name*/'铅笔斧头·复制品', /*description*/'优妮因为虚荣和异想天开而携带的矛枪\n的仿造品。虽然表面上看着很重，\n但其实是小孩子也能够放心使用的轻量设计。因为无法自己站立，\n一旦失去支撑，就会啪塔一声倒下。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/56.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/200.0, /*wave_energy_recovery*/30.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/1.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131111, /*equipment_name*/'万圣节猫咪扫帚', /*description*/'镜华为了万圣节悄悄准备的\n以黑猫为主题的扫帚。有时会瞒着大家，\n想象着自己乘坐扫帚飞上天空。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/96.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/1.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131121, /*equipment_name*/'魔拳不给糖果就捣蛋', /*description*/'未奏希为了万圣节自信满满准备的\n用于恶作剧的特别\n护手。同时具备高强度和高精密度，\n无论是什么恶作剧都能完美实现。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/8.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131131, /*equipment_name*/'圈圈兔兔剑', /*description*/'美美为了万圣节，和妈妈一起制作的\n用绑带一圈一圈卷起的\n兔子剑。似乎同时提升了\n可爱度和手感。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/33.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/12.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
