INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131161, /*equipment_name*/'圣灵剑神圣希望', /*description*/'成为圣诞老人候选人的望所持有的单\n手剑。作为偶像的决意，以及想给许\n多人带去梦想与希望的心意，\n给这把剑带来了耀眼的光辉。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/290.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/12.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/4.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131171, /*equipment_name*/'圣夜斧圣诞尖牙', /*description*/'成为圣诞老人候选人的伊莉亚所爱用\n的斧子，在圣夜的奇迹下变成了更有\n魅力的新姿态。在柊树华丽的装点\n下，巨刃发出不灭的光芒。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/100.0, /*atk*/0.0, /*magic_str*/75.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131191, /*equipment_name*/'新春祈福的精灵杖', /*description*/'身穿新年盛装的可可萝持有的充满神\n气的神圣之杖。庆祝新春的巫女之舞\n将活力分给了周围的同伴，\n新的一年一定能够幸福。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/30.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/1.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131201, /*equipment_name*/'贺年猫耳的毽子板', /*description*/'身穿新春盛装的凯露持有的特别定制\n的毽子板。祈愿新一年能够幸福的心\n情让板上生出无数的羽毛，祓除一切\n灾害。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/74.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131211, /*equipment_name*/'神殿巫女的铃铛', /*description*/'在神殿担任巫女的铃莓所持有的神乐\n铃。其中倾注了世世代代巫女们的祈\n愿，能够净化铃声所及之处的\n所有邪气。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/67.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
