INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131231, /*equipment_name*/'魔法纯真弓箭', /*description*/'魔法猎人Purely★栞使用的\n魔法之弓通过爱与希望的力量，\n可以释放出贯穿任何邪恶敌人\n的必杀箭矢。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/84.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/40.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131241, /*equipment_name*/'可爱舞台', /*description*/'卯月持有的正统派的剑。\n虽然看上去没有特别的力量，\n但会根据卯月的笑容和温暖的心意，\n绽放出光辉，发挥出隐藏的力量。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/82.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/34.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/1.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131251, /*equipment_name*/'冷酷舞台', /*description*/'凛使用的美丽的蓝色之剑。\n与凛那静静燃烧的炽热之心相呼应，\n绽放出蓝色的光辉，照亮以最高处\n为目标不断努力的她的旅途。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/220.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/8.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131261, /*equipment_name*/'激情舞台', /*description*/'未央爱用的闪耀星杖。未央明快的\n歌声呼唤出小小的流星，\n歌唱时的模样就如同太空歌剧一样。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/90.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/16.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131271, /*equipment_name*/'橡子游骑兵之矛', /*description*/'一支充满了母爱的长矛，\n是和她的盔甲一起买的。有着傲人的非凡硬度，\n但同时重量很轻，\n铃的力量也可以随意挥舞它。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/78.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/25.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/131281, /*equipment_name*/'伊丽莎白之叉', /*description*/'牧场世世代代流传下来的正统装备。\n虽然是用来保护牧场不受魔物的伤害，\n但其形状与平时用于照料动物的\n农具很相似。', /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/87.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/26.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
