INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130011, /*equipment_name*/'喵王腰带', /*description*/'日和莉所佩戴的腰带。\n带扣上是一只怒目圆睁的野兽，\n能够鼓舞战斗本能，赋予\n她的双拳更强的力量。', /*promotion_level*/98, /*hp*/0.0, /*atk*/4.7, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130021, /*equipment_name*/'绽放希望', /*description*/'优衣所佩戴的发箍。\n上面可爱的花型蕾丝\n与装饰提高了守护同伴\n的治愈之力。', /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.3, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130031, /*equipment_name*/'盖尔手套', /*description*/'怜经常使用的的手套。穿上这对\n攻守合一的手套所打出\n的每一拳，会击溃挡在\n前方的任何敌人。', /*promotion_level*/98, /*hp*/5.5, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130051, /*equipment_name*/'正义勋章', /*description*/'颁授自骑士团\n的勋章。茉莉非常珍惜它。每当看到闪闪发光的勋章，\n茉莉的正义感就会倍增，\n无论面对何种恶势力都绝不退缩。', /*promotion_level*/98, /*hp*/6.5, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.4, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130061, /*equipment_name*/'恶魔三叉戟', /*description*/'茜里十分珍爱的一把魔枪。武\n器中蕴藏的强大魔力，可让小恶\n魔华丽地变身成性感的\n魔女。', /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.6, /*def*/0.15, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130091, /*equipment_name*/'螺旋灵击疾风飞剑', /*description*/'疾风之冥姬所使用的魔装。凡人使用的\n瞬间就会被魔法吞噬变成废人。\n当刀刃上闪耀起不祥的深蓝色光芒时，\n黑暗将化为疾风，把愚者送入地狱。', /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.5, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130111, /*equipment_name*/'阳焰之箭', /*description*/'璃乃专用的魔法箭，寄宿着炽烈的火焰。\n火焰来源于热情的内心，\n每一箭，都有着令其引以为傲的\n高威力，拥有一招制胜的潜力。', /*promotion_level*/98, /*hp*/0.0, /*atk*/4.6, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130121, /*equipment_name*/'流星☆蝴蝶结', /*description*/'妹妹栞亲手做来送给初音的\n蝴蝶结。星星里寄托了栞的思念，\n与姐姐的魔力相呼应\n能降低敌方魔法防御力。', /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.4, /*def*/0.05, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130151, /*equipment_name*/'神爱头纱', /*description*/'美里所佩戴的头纱。\n美里的慈爱从圣洁的头纱之中\n流溢而出，振奋众人精神，\n能提升魔法暴击。', /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.55, /*def*/0.15, /*magic_def*/0.35, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.15, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130171, /*equipment_name*/'球阳唐手秘籍', /*description*/'记载着香织所施展的琉球空手道奥义\n的卷轴。据说，上面施加有特殊的法术，只会对高手的练气产生反应，\n并浮现出\n文字。', /*promotion_level*/98, /*hp*/0.0, /*atk*/4.7, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130181, /*equipment_name*/'性感之鞭', /*description*/'伊绪当作教鞭使用的鞭子。\n不管是多乏味的学业，\n一经伊绪之手\n就会变成甜蜜的时光。', /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.25, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130211, /*equipment_name*/'纯洁铃铛', /*description*/'胡桃当作护身符的铃铛。\n从外观庄严的铃铛中奏出的\n清脆铃声，能给予怯懦的心灵\n勇气。', /*promotion_level*/98, /*hp*/5.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.4, /*magic_def*/0.4, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/10.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130221, /*equipment_name*/'恶魔之矛', /*description*/'这把魔枪专门增幅依里\n的魔力。用生命力作为代价获得的\n魔力，强到一挥枪即可将周围\n化为灰烬。', /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.25, /*def*/0.0, /*magic_def*/0.05, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130261, /*equipment_name*/'宝石浆栎果', /*description*/'这条项链装饰有传说中蕴藏有强大魔力的\n浆栎果。能增强铃所释放的\n魔法，并大幅提升周围同伴的能力。', /*promotion_level*/98, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.1, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.5, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130271, /*equipment_name*/'残酷之斧', /*description*/'这把灵斧能提炼惠理子那疯狂的爱意，\n能将过激的爱情转化成破坏力。\n每次挥舞斧光，便会奏响毁灭的\n狂想曲。', /*promotion_level*/98, /*hp*/0.0, /*atk*/4.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130281, /*equipment_name*/'辉煌羽饰', /*description*/'咲恋经常佩戴的发饰。\n象征着潜藏在她温柔内心中的\n坚强。当羽翼展翅之时，\n身体将不受重力束缚。', /*promotion_level*/98, /*hp*/1.25, /*atk*/5.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130291, /*equipment_name*/'华丽麦克风', /*description*/'望专用的手持麦克风。可爱的\n蝴蝶结中间画着\n【慈乐之音】的记号。将它紧握\n在手，朝顶级偶像努力。', /*promotion_level*/98, /*hp*/15.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.3, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.05, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130301, /*equipment_name*/'红莲爆炎扇', /*description*/'妮侬惯用的军配团扇。\n鲜红的团扇所掀起的狂风\n携带着爆炎，直到将阻挡在前的\n所有事物燃烧殆尽才消逝。', /*promotion_level*/98, /*hp*/0.0, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130311, /*equipment_name*/'暗灵法袍', /*description*/'忍爱穿的灵衣，\n萦绕着蓝色灵力之火。\n用能被灵附身的丝线编织而成，\n可得到幽灵们的助力。', /*promotion_level*/98, /*hp*/12.5, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130321, /*equipment_name*/'紫藤庐红辉石', /*description*/'秋乃佩戴在身的胸针。紫藤\n庐家代代相传的辉石，\n会赐予持有者高洁的精神\n和与众不同的魔力。', /*promotion_level*/98, /*hp*/7.5, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.1, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130331, /*equipment_name*/'伊丽莎白披巾', /*description*/'以真阳心爱的牛·伊丽莎白为原型\n进行设计的披巾。戴上吼\n逗哏能力增强，能令对方\n陷入混乱。', /*promotion_level*/98, /*hp*/0.0, /*atk*/4.25, /*magic_str*/0.0, /*def*/0.05, /*magic_def*/0.05, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130341, /*equipment_name*/'无尽酒杯', /*description*/'由加莉不愿放下的酒杯。不管\n怎么喝都不会枯竭的生命之水\n渗透了持有者的全身，赐予\n其身仿佛要满溢而出的活力。', /*promotion_level*/98, /*hp*/0.0, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.6, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130371, /*equipment_name*/'天剑白岚', /*description*/'御久间流传承的宝剑。\n剑身被打造得如同羽毛一般轻盈，\n与智的出招相呼应，掀起\n横扫的旋风席卷众多敌人。', /*promotion_level*/98, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.2, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130381, /*equipment_name*/'初音亲手制作的守护石', /*description*/'姐姐初音亲手做来送给栞\n的守护石。其中蕴含着初音的思念，\n能够激起妹妹的勇气，让她发挥出\n异于平常的能力。', /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.2, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.15, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130401, /*equipment_name*/'BB团羁绊贝雷帽', /*description*/'碧时常戴着的贝雷帽。\n搭配的一对羽毛象征着紧密的羁\n绊。心中交叠着羁绊，碧会\n凭借坚强的意志击穿拦路的敌人。', /*promotion_level*/98, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130431, /*equipment_name*/'狼人巨刃', /*description*/'寄宿着青狼灵魂的巨剑。象征着\n装备者的勇敢的一挥\n犹如龙卷风向敌人袭去，同时\n唤醒沉睡的力量。', /*promotion_level*/98, /*hp*/0.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.6, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130461, /*equipment_name*/'梦幻鲷鱼烧', /*description*/'珠希非常珍惜的项链。\n使用镶嵌在鲷鱼烧型的\n小饰物上的魔石，就能获得快到\n留下残影的迅猛身手。', /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.25, /*physical_critical*/0.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130471, /*equipment_name*/'骑士铠甲', /*description*/'上下缠绕着正义的热焰的铠甲。\n不管多强力的攻击都能够\n阻挡住，并给予同伴勇气\n与活力。', /*promotion_level*/98, /*hp*/0.0, /*atk*/1.0, /*magic_str*/0.0, /*def*/0.4, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130481, /*equipment_name*/'溟龙枪利维坦', /*description*/'美冬爱用的长戟。\n每次挥动枪尖就会涌动水流，\n水之庇佑会\n激昂又优美地保护持有者。', /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.15, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130491, /*equipment_name*/'神圣十字剑', /*description*/'静流惯用的十字剑。\n祈愿救助弱者的高洁灵魂\n注入剑身时，可以发动防御招式，\n守护对方。', /*promotion_level*/98, /*hp*/0.0, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130511, /*equipment_name*/'蔷薇诅咒', /*description*/'深月惯用的眼罩，上面装饰着\n妖艳美丽的蔷薇。\n注入魔力就会释放七彩光芒，\n衰弱对手的能力。', /*promotion_level*/98, /*hp*/5.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130521, /*equipment_name*/'野心礼服', /*description*/'我想要穿上这条裙子…！这条特别定制的\n长裙承载着莉玛\n的梦想。怀梦少女的妄想力，\n此刻觉醒。', /*promotion_level*/98, /*hp*/0.0, /*atk*/1.25, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.35, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130581, /*equipment_name*/'公主之剑', /*description*/'佩可莉姆所使用的『皇家装备』的\n真正形态。本就拥有很强的力量，\n而今因沉睡之力被释放，\n更是被赋予了愈加强大的力量。', /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.5, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130591, /*equipment_name*/'爱梅斯守护符', /*description*/'可可萝佩戴的花饰。\n受到了被尊为神明的伟大存在\n的守护，花朵中飘荡出的温柔\n花香，赋予人战斗的勇气。', /*promotion_level*/98, /*hp*/0.0, /*atk*/2.5, /*magic_str*/0.0, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130601, /*equipment_name*/'混沌魔法书', /*description*/'凯露随身携带的魔法书。通过控制\n快要从书中喷涌而出的魔力\n便可以自由自在地操纵\n强大的招数。', /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.7, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130631, /*equipment_name*/'女王缇达尼亚斯', /*description*/'亚里莎惯用的巨弓。是在\n精灵之间流传的传说之弓，\n仅凭拉弓就可封住目标的动作，所\n释放的箭矢能射穿任何东西。', /*promotion_level*/98, /*hp*/0.0, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.8, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
