INSERT INTO `unit_comments` VALUES (/*id*/1049006, /*unit_id*/104931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'让可爱的弟弟犯愁的人，\n姐姐会将他们全~部惩处的！');
