INSERT INTO `unit_comments` VALUES (/*id*/1049008, /*unit_id*/104931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'姐姐是为了保护弟弟才变强的\n姐姐是弟弟的盾，弟弟的剑，\n也是弟弟的盔甲');
INSERT INTO `unit_comments` VALUES (/*id*/1049009, /*unit_id*/104931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'打扰我与弟弟共度时光\n不识趣的家伙，姐姐决不原谅！\n这可不是光吃一记手刀\n就能完事的！');
INSERT INTO `unit_comments` VALUES (/*id*/1049010, /*unit_id*/104931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'竟敢欺负弟弟，\n即使神明允许，\n老天饶恕，\n姐姐也不会原谅！');
