INSERT INTO `unit_comments` VALUES (/*id*/1060015, /*unit_id*/106061, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'哼哼哼……怎么样啊，这股力量！\n可以再多夸夸我……呜呀！\n别、别突然摸我的头！\n宰、宰了你哦！');
INSERT INTO `unit_comments` VALUES (/*id*/1060016, /*unit_id*/106061, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'和那家伙的情谊化作了力量……\n这样也不错嘛。\n什么！原来你听到了吗！？\n赶、赶紧给我忘掉！');
INSERT INTO `unit_comments` VALUES (/*id*/1060017, /*unit_id*/106061, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我会用这股全新的力量\n来保护你……那个……\n千、千万不要\n从我身边离开哦！');
INSERT INTO `unit_comments` VALUES (/*id*/1061001, /*unit_id*/106101, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/2.2, /*change_face*/2, /*description*/'嗯？啊，是你啊！\n找我有什么事？\n遇上了麻烦的话要跟我说哦？');
INSERT INTO `unit_comments` VALUES (/*id*/1061002, /*unit_id*/106101, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.8, /*change_face*/2, /*description*/'你失忆了啊……\n希望你能早点恢复记忆！\n我去帮你找找有效的药！');
INSERT INTO `unit_comments` VALUES (/*id*/1061003, /*unit_id*/106101, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/'也把你的公主骑士之力\n用在我身上嘛！\n原先还是你敌人的时候，\n我每次看到都很兴奋！');
INSERT INTO `unit_comments` VALUES (/*id*/1061004, /*unit_id*/106101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/2.9, /*change_face*/2, /*description*/'虽然在不久前还为各种事情不安，\n但是现在已经没事了！\n因为能和你还有大家互帮互助呀！');
INSERT INTO `unit_comments` VALUES (/*id*/1061005, /*unit_id*/106101, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.1, /*change_face*/3, /*description*/'没想到曾经互为仇敌的你和我\n现在竟成了同伴……\n所以，尾狗刀也一定……');
INSERT INTO `unit_comments` VALUES (/*id*/1061006, /*unit_id*/106131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'你们全都给我让开！\n敢挡路的家伙，\n我统统斩了！');
INSERT INTO `unit_comments` VALUES (/*id*/1061007, /*unit_id*/106131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'新的天楼霸断剑吗……\n不管是什么样的对手，\n有了它我就是无敌的！上啊！');
INSERT INTO `unit_comments` VALUES (/*id*/1061008, /*unit_id*/106131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我不会再逃避躲藏了！\n就算遍体鳞伤，\n我都会战斗到最后！我意已决！');
INSERT INTO `unit_comments` VALUES (/*id*/1061009, /*unit_id*/106131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'哈、哈、哈！\n闪开闪开！\n诺维姆大人驾到！');
INSERT INTO `unit_comments` VALUES (/*id*/1061010, /*unit_id*/106131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'终于和你成为同伴了啊。\n真希望能快点让尾狗刀清醒，\n和他一起战斗啊！');
INSERT INTO `unit_comments` VALUES (/*id*/1061011, /*unit_id*/106101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/2.1, /*change_face*/5, /*description*/'生日快乐！\n我没怎么给人送过东西……\n你想要什么？');
INSERT INTO `unit_comments` VALUES (/*id*/1061012, /*unit_id*/106131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'Happy birthday！\n好嘞！去买蛋糕吧！\n今天我请客！\n大概明天会下雪哦！');
INSERT INTO `unit_comments` VALUES (/*id*/1063001, /*unit_id*/106301, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/5.8, /*change_face*/1, /*description*/'兰德索尔好厉害……\n什么都有，面积还很大……\n跟我的故乡完全不一样');
