INSERT INTO `unit_comments` VALUES (/*id*/1063006, /*unit_id*/106331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'总有一天，我会成为森林的守卫\n当然，是和萝沙丽亚一起！');
INSERT INTO `unit_comments` VALUES (/*id*/1063007, /*unit_id*/106331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'兰德索尔很广阔……\n不过，这里的风的气息和故乡的很像');
