INSERT INTO `unit_comments` VALUES (/*id*/1026007, /*unit_id*/102631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'啊啊，这个空间简直是\n为了我而\n存在的……\n我都不想回去了');
INSERT INTO `unit_comments` VALUES (/*id*/1026008, /*unit_id*/102631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'虽然柔软的床也很棒\n不过在自然的包围之中悠闲度过\n也别有一番乐趣……');
