INSERT INTO `unit_comments` VALUES (/*id*/1026010, /*unit_id*/102631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'谢谢你带我\n到这么棒的地方来\n如果是来这里，\n我一百个愿意出门，下次再带我来吧');
INSERT INTO `unit_comments` VALUES (/*id*/1026011, /*unit_id*/102601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.2, /*change_face*/2, /*description*/'生日快乐~\n我没有忘记买礼物哦~\n我指定了送货的时间，\n所以应该很快就会送到啦~');
INSERT INTO `unit_comments` VALUES (/*id*/1026012, /*unit_id*/102631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'生日快乐~\n正因为是特别的日子，\n才要悠闲度过呀\n我也会陪你的哦~');
