INSERT INTO `unit_comments` VALUES (/*id*/1028004, /*unit_id*/102801, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/3.5, /*change_face*/5, /*description*/'下次，我想和你\n找个地方一起玩玩呢\n啊，当然，\n是不用花钱的地方哦？');
INSERT INTO `unit_comments` VALUES (/*id*/1028005, /*unit_id*/102801, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/2.0, /*change_face*/2, /*description*/'我打算举办一个茶话会\n当然了，\n也会邀请你的\n敬请期待！');
INSERT INTO `unit_comments` VALUES (/*id*/1028006, /*unit_id*/102831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'限定道具的队尾在这里……\n哎呀，真巧呀你也来看看吧？\n这是我们主办的义卖会！');
INSERT INTO `unit_comments` VALUES (/*id*/1028007, /*unit_id*/102831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我去吆喝招揽客人，\n你能举着这块指示牌引导他们吗？');
INSERT INTO `unit_comments` VALUES (/*id*/1028008, /*unit_id*/102831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'来的客人比想象的还要多，\n吓了我一跳！机会难得\n你也来看看吧！');
INSERT INTO `unit_comments` VALUES (/*id*/1028009, /*unit_id*/102831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'谢谢你帮我整队♪\n和你在一起工作\n就会比平时开心好多倍呀♪\n下次也拜托你了！');
INSERT INTO `unit_comments` VALUES (/*id*/1028010, /*unit_id*/102831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'义卖会盛况非凡呢?\n待会儿赶快去吃饭吧！\n有你在，疲劳都烟消云散了♪');
INSERT INTO `unit_comments` VALUES (/*id*/1028011, /*unit_id*/102801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.1, /*change_face*/2, /*description*/'Happy birthday♪\n礼物是救济院的大家\n挑的哦。当然我也有参与\n心怀感激地收下吧！');
INSERT INTO `unit_comments` VALUES (/*id*/1028012, /*unit_id*/102831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今天是你的生日对吧？\n我们出去庆祝吧\n这种日子就该好好休息\n偶尔也要享受一下♪');
INSERT INTO `unit_comments` VALUES (/*id*/1028013, /*unit_id*/102861, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'大家能够开心地露出笑容，\n这比其他任何事都让人感到高兴。\n能够在这样的地方与你一同度过，\n我真的很幸福。');
