INSERT INTO `unit_comments` VALUES (/*id*/1047003, /*unit_id*/104701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我喜欢运动\n不过不擅长游泳\n因为盔甲太重了……');
