INSERT INTO `unit_comments` VALUES (/*id*/1047007, /*unit_id*/104731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'无论对手是怎样的强敌\n为了孩子们的笑容\n我都会无所畏惧地面对');
