INSERT INTO `unit_comments` VALUES (/*id*/1047010, /*unit_id*/104731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'身为团长，我必须平等地守护\n人们的安宁，可只有对你\n我会忍不住将你放在第一位……');
INSERT INTO `unit_comments` VALUES (/*id*/1047011, /*unit_id*/104701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'听说今天是你的生日啊\n既然都长大一岁了，今年\n就不要那么乱来了哦？\n我会担心的');
INSERT INTO `unit_comments` VALUES (/*id*/1047012, /*unit_id*/104731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'生日快乐！你有什么\n愿望吗？那、那\n可不行啊少年！竟然要我脱掉头盔……\n我会害羞到爆炸的！');
INSERT INTO `unit_comments` VALUES (/*id*/1048001, /*unit_id*/104801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.9, /*change_face*/2, /*description*/'无论是赚素材还是赚金币……\n两方的战略我都制定好了\n有需要就来找我商量吧');
