INSERT INTO `unit_comments` VALUES (/*id*/1048003, /*unit_id*/104801, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.5, /*change_face*/1, /*description*/'高效行动的关键\n在于观察力哦\n你平时有认真锻炼吗？');
