INSERT INTO `unit_comments` VALUES (/*id*/1048005, /*unit_id*/104801, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/5.8, /*change_face*/2, /*description*/'你的日程，\n要不要交给我来管理？\n我会把24小时都安排好的\n当然了，管理费可是要付的');
INSERT INTO `unit_comments` VALUES (/*id*/1048006, /*unit_id*/104831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'不介意的话，你的钱包\n交给我管理吧？\n诶？我才不会偷钱呢！\n不要误会我！');
