INSERT INTO `unit_comments` VALUES (/*id*/1089012, /*unit_id*/108931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今天是你诞生在这个世上的\n特别的日子。让我为你献上\n特别的祝福吧。不管什么都行\n……除了假装亲吻……');
INSERT INTO `unit_comments` VALUES (/*id*/1090001, /*unit_id*/109001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/12.7, /*change_face*/2, /*description*/'亲爱的。这是我亲手做的巧克力，\n您能收下吗？\n里面包含了我满满的爱意。\n来，请张开嘴……');
INSERT INTO `unit_comments` VALUES (/*id*/1090002, /*unit_id*/109001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/10.8, /*change_face*/2, /*description*/'很香对吧？\n我在里面加了香草。\n您问有什么功能？\n嘻嘻……您猜呢？');
INSERT INTO `unit_comments` VALUES (/*id*/1090003, /*unit_id*/109001, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/3.7, /*change_face*/2, /*description*/'您在介意周围的小瓶子吗？\n请放心，那些都是调味料。\n烹饪的时候想要用最顺手的器具，\n这不过是少女心而已。');
INSERT INTO `unit_comments` VALUES (/*id*/1090004, /*unit_id*/109001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.2, /*change_face*/5, /*description*/'真不可思议，\n做巧克力的时候，\n心想着要看到您高兴的表情，\n就变得拼命起来了。');
INSERT INTO `unit_comments` VALUES (/*id*/1090005, /*unit_id*/109001, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/11.2, /*change_face*/2, /*description*/'像这样为您做料理，\n仿佛我就是您真正的伴侣一样呢。\n当然了，总有一天会成真的……嘻嘻。');
INSERT INTO `unit_comments` VALUES (/*id*/1090006, /*unit_id*/109031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'所谓情人节，\n是把巧克力送给心爱之人的日子……\n让我用甜蜜的爱\n填满您的肚子吧。');
INSERT INTO `unit_comments` VALUES (/*id*/1090007, /*unit_id*/109031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'仅凭巧克力无法完全表达\n我对您这位真命天子的爱意。\n我要附上一封\n花了三天三夜、饱含爱意的信……');
INSERT INTO `unit_comments` VALUES (/*id*/1090008, /*unit_id*/109031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我穿围裙好看吗？\n嘻嘻……我好开心。\n这样的话，我每天都穿给您看。\n不论时间，不论地点……');
INSERT INTO `unit_comments` VALUES (/*id*/1090009, /*unit_id*/109031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'情人节这天，\n听说也有人会在送巧克力时告白。\n但是……就算不是特别的日子，\n我也会每天向您示爱的哦。');
INSERT INTO `unit_comments` VALUES (/*id*/1090010, /*unit_id*/109031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'虽然加入我调制的药也是个好办法，\n但是，最能让巧克力变得甘甜的\n果然还是其中蕴藏的爱意。\n我爱您……亲爱的。');
INSERT INTO `unit_comments` VALUES (/*id*/1090011, /*unit_id*/109001, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/11.2, /*change_face*/2, /*description*/'您诞生的日子……\n也就是我们的命运开始的那一天。\n让我们举杯庆祝吧。\n来，请饮下这杯……');
INSERT INTO `unit_comments` VALUES (/*id*/1090012, /*unit_id*/109031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'祝您生日快乐。\n我已经准备好了\n只有两个人的庆祝会。\n在那之后……嘻嘻。');
INSERT INTO `unit_comments` VALUES (/*id*/1091001, /*unit_id*/109101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.7, /*change_face*/2, /*description*/'弟弟，你知道情人节吗？\n那是给最喜欢的人\n送巧克力的日子。\n姐姐的爱，你要收下哦？');
INSERT INTO `unit_comments` VALUES (/*id*/1091002, /*unit_id*/109101, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/7.5, /*change_face*/5, /*description*/'和姐姐的宠溺一样甜蜜的巧克力，\n或者是和姐姐的摸摸一样，\n只有大人才能品尝的巧克力，\n弟弟你喜欢哪一种呢？');
